#include <iostream>
#include "empregado.h"
#include <vector>

using namespace std;

int main(){

    string x;
    float n = 1;
    vector <Empregado> eu;

    do{
        Empregado temp;
        temp.obterDados();
        eu.push_back(temp);

        cout << "Deseja continua? [s] ou [n]: ";
        cin >> x;

    }while(x == "s");

    //// IMPRIME SALARIO

    for(int i = 0; i < eu.size(); i++){
        eu[i].mostraDados(n);
    }

    //// AUMENTO DE 10% NO SALARIO
    
    n = 1.10; 

    for(int i = 0; i < eu.size(); i++){
        eu[i].mostraDados(n);
    }

    return 0;
}

